import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import { ThumbsUp, ThumbsDown } from "lucide-react";

function App() {
  const [code, setCode] = useState("");
  const [output, setOutput] = useState("");
  const [loading, setLoading] = useState(false);
  const [reviewId, setReviewId] = useState(null);
  const [feedbackMode, setFeedbackMode] = useState(false);
  const [selectedReason, setSelectedReason] = useState("");
  const outputRef = useRef(null);

  const handleSubmit = async () => {
    if (!code.trim()) {
      setOutput("‚ùå Please enter some code to review.");
      return;
    }
    setLoading(true);
    setOutput("");
    try {
      const response = await axios.post("http://localhost:8000/generate-review", { code });
      const { review, optimized_code, explanation, security_issues, review_id } = response.data;
      const currentTime = new Date().toLocaleString("en-IN", { timeZone: "Asia/Kolkata" });

      const outputText = `
        <div class='mt-4 p-4 bg-[#f7f7f8] border border-gray-300 rounded shadow-sm'>
          <p class='text-gray-800 font-semibold'>Review (${currentTime})</p>
          <pre class='text-gray-800 font-mono text-sm bg-white p-3 rounded mt-2 overflow-auto max-h-60 border border-gray-200'>${highlightKeywords(code)}</pre>
          <p class='text-gray-700 mt-4'><strong>Review:</strong> ${review}</p>
          ${optimized_code ? `<p class='text-gray-700 mt-2'><strong>Optimized Code:</strong><br/><pre class='bg-gray-100 p-2 rounded mt-1 overflow-auto max-h-48'>${highlightKeywords(optimized_code)}</pre></p>` : ""}
          ${explanation ? `<p class='text-gray-700 mt-2'><strong>Explanation:</strong> ${explanation}</p>` : ""}
          ${security_issues ? `<p class='text-gray-700 mt-2'><strong>Security Issues:</strong> ${security_issues}</p>` : ""}
        </div>
      `;
      setOutput(outputText);
      setReviewId(review_id);
    } catch (error) {
      setOutput(`‚ùå Error: ${error.response?.data?.error || "Failed to generate review."}`);
    } finally {
      setLoading(false);
      if (outputRef.current) {
        outputRef.current.scrollTop = outputRef.current.scrollHeight;
      }
    }
  };

  const highlightKeywords = (text) => {
    const keywords = ["function", "class", "return", "if", "else", "for", "while", "try", "catch", "import", "const", "let", "var", "public", "private", "static", "void", "int", "String"];
    const regex = new RegExp(`\\b(${keywords.join("|")})\\b`, "g");
    return text.replace(regex, '<span class="text-blue-500 font-semibold">$1</span>');
  };

  const handleAccept = async () => {
    if (!reviewId) {
      setOutput((prev) => `${prev}\n‚ùå No review to accept.`);
      return;
    }
    try {
      await axios.post("http://localhost:8000/submit-feedback", {
        review_id: reviewId,
        feedback: "Accepted",
      });
      setOutput((prev) => `${prev}\n<p class='text-green-500 mt-2'>‚úÖ Review accepted!</p>`);
    } catch (error) {
      setOutput((prev) => `${prev}\n‚ùå Error: ${error.response?.data?.error || "Failed to submit feedback."}`);
    }
    if (outputRef.current) outputRef.current.scrollTop = outputRef.current.scrollHeight;
  };

  const handleReject = () => {
    setFeedbackMode(true);
  };

  const handleFeedbackSubmit = async () => {
    if (!selectedReason) {
      setOutput((prev) => `${prev}\n‚ùå Please select a rejection reason.`);
      return;
    }
    try {
      await axios.post("http://localhost:8000/submit-feedback", {
        review_id: reviewId,
        feedback: "Rejected",
        rejection_reason: selectedReason,
      });
      setOutput((prev) => `${prev}\n<p class='text-red-500 mt-2'>üì© Feedback submitted: ${selectedReason}</p>`);
      setFeedbackMode(false);
      setSelectedReason("");
    } catch (error) {
      setOutput((prev) => `${prev}\n‚ùå Error: ${error.response?.data?.error || "Failed to submit feedback."}`);
    }
    if (outputRef.current) outputRef.current.scrollTop = outputRef.current.scrollHeight;
  };

  useEffect(() => {
    if (outputRef.current) {
      outputRef.current.scrollTop = outputRef.current.scrollHeight;
    }
  }, [output]);

  return (
    <div className="h-screen w-full bg-white text-gray-800 flex flex-col">
      <div className="bg-[#f7f7f8] border-b border-gray-300 px-4 py-3 shadow-sm">
        <h1 className="text-xl font-bold text-center">AI Code Reviewer</h1>
      </div>
      <div className="flex-1 overflow-auto px-4 py-4 flex flex-col items-center">
        <div className="w-full max-w-3xl">
          <textarea
            rows={6}
            value={code}
            onChange={(e) => setCode(e.target.value)}
            className="w-full bg-[#f1f1f1] border border-gray-300 rounded p-3 text-sm font-mono focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="Paste your code here..."
          />
          <button
            onClick={handleSubmit}
            disabled={loading}
            className="w-full bg-blue-600 text-white px-4 py-2 rounded mt-3 hover:bg-blue-700 transition disabled:opacity-50"
          >
            {loading ? "Processing..." : "Submit for Review"}
          </button>
          <div
            ref={outputRef}
            className="mt-6 overflow-y-auto max-h-[50vh]"
            dangerouslySetInnerHTML={{ __html: output }}
          />

          {reviewId && !feedbackMode && (
            <div className="mt-4 flex justify-end gap-4">
              <button
                onClick={handleAccept}
                className="flex items-center gap-1 text-green-600 hover:text-green-700"
              >
                <ThumbsUp size={16} /> Accept
              </button>
              <button
                onClick={handleReject}
                className="flex items-center gap-1 text-red-600 hover:text-red-700"
              >
                <ThumbsDown size={16} /> Reject
              </button>
            </div>
          )}

          {feedbackMode && (
            <div className="mt-4">
              <label className="block text-sm font-medium text-gray-700">Rejection Reason</label>
              <select
                value={selectedReason}
                onChange={(e) => setSelectedReason(e.target.value)}
                className="w-full border border-gray-300 rounded p-2 mt-1 bg-white text-sm"
              >
                <option value="">Select a reason</option>
                <option value="Inaccurate review">Inaccurate review</option>
                <option value="Optimization error">Optimization error</option>
                <option value="Missing explanation">Missing explanation</option>
                <option value="Security oversight">Security oversight</option>
                <option value="Other">Other</option>
              </select>
              <button
                onClick={handleFeedbackSubmit}
                className="w-full bg-red-600 text-white px-4 py-2 rounded mt-3 hover:bg-red-700"
              >
                Submit Feedback
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
