import React, { useState, useRef } from 'react';
import { Plus, Link, Upload, Send, ThumbsDown } from 'lucide-react';

const CodeReviewApp = () => {
  const [showDropdown, setShowDropdown] = useState(false);
  const [codeInput, setCodeInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [reviewData, setReviewData] = useState(null);
  const [showFeedback, setShowFeedback] = useState(false);
  const [showThanks, setShowThanks] = useState(false);
  const fileInputRef = useRef(null);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setCodeInput(e.target.result);
        setShowDropdown(false);
      };
      reader.readAsText(file);
    }
  };

  const handleSubmitCode = async () => {
    if (!codeInput.trim()) return;
    
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:8000/generate-review', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ code: codeInput }),
      });
      
      const data = await response.json();
      setReviewData(data);
    } catch (error) {
      console.error('Error:', error);
    }
    setIsLoading(false);
  };

  const handleFeedbackSubmit = async (feedback, rejectionReason = null) => {
    try {
      await fetch('http://localhost:8000/submit-feedback', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          review_id: reviewData.review_id,
          feedback,
          rejection_reason: rejectionReason
        }),
      });
      setShowThanks(true);
      setShowFeedback(false);
    } catch (error) {
      console.error('Error submitting feedback:', error);
    }
  };

  const feedbackTags = [
    'Syntax errors present',
    'Security vulnerabilities found',
    'Incorrect output / results',
    'Code logic is incorrect',
    'Poor or missing comments',
    'Code style inconsistent',
    'Inefficient algorithm',
    'Unnecessary complexity',
    'Duplicate / redundant code',
    'Variable/method naming not clear',
    'Does not handle edge cases',
    'Other (optional text box)'
  ];

  const detectLanguage = (code) => {
    if (code.includes('def ') || code.includes('print(') || code.includes('import ')) return 'python';
    if (code.includes('function') || code.includes('console.log') || code.includes('const ')) return 'javascript';
    if (code.includes('public class') || code.includes('System.out.println')) return 'java';
    if (code.includes('#include') || code.includes('cout <<')) return 'cpp';
    return 'text';
  };

  if (showThanks) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-stone-800 to-stone-900 p-6">
        {/* Logo */}
        <div className="flex items-center gap-3 mb-12">
          <div className="w-12 h-12 bg-white rounded-full flex items-center justify-center">
            <div className="w-8 h-8 bg-stone-800 rounded flex items-center justify-center">
              <div className="w-4 h-4 border-2 border-white rounded-sm"></div>
            </div>
          </div>
          <span className="text-white text-xl font-semibold">CRVKN</span>
        </div>

        <div className="max-w-4xl mx-auto">
          {/* User Input */}
          <div className="flex justify-end mb-6">
            <div className="bg-white rounded-full px-6 py-3 max-w-md">
              <span className="text-stone-800">{codeInput.substring(0, 50)}...</span>
            </div>
          </div>

          {/* Review Content */}
          <div className="space-y-6 text-white">
            <h2 className="text-xl font-medium">Your line</h2>
            
            <div className="bg-stone-700/50 rounded-xl p-6">
              <div className="text-sm text-stone-300 mb-2">{detectLanguage(codeInput)}</div>
              <pre className="text-white font-mono">{codeInput}</pre>
            </div>

            <div className="space-y-4">
              <p>{reviewData?.review}</p>
              
              {reviewData?.optimized_code && (
                <div className="bg-stone-700/50 rounded-xl p-6">
                  <div className="text-sm text-stone-300 mb-2">{detectLanguage(reviewData.optimized_code)}</div>
                  <pre className="text-white font-mono">{reviewData.optimized_code}</pre>
                </div>
              )}

              {reviewData?.explanation && (
                <p>{reviewData.explanation}</p>
              )}

              {reviewData?.security_issues && (
                <div className="bg-stone-700/50 rounded-xl p-6">
                  <div className="text-sm text-stone-300 mb-2">Security Analysis</div>
                  <p className="text-white">{reviewData.security_issues}</p>
                </div>
              )}
            </div>

            <div className="pt-8">
              <p className="text-stone-300 italic">"Thanks for your feedback â€” it helps us improve."</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (reviewData && !showThanks) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-stone-800 to-stone-900 p-6">
        {/* Logo */}
        <div className="flex items-center gap-3 mb-12">
          <div className="w-12 h-12 bg-white rounded-full flex items-center justify-center">
            <div className="w-8 h-8 bg-stone-800 rounded flex items-center justify-center">
              <div className="w-4 h-4 border-2 border-white rounded-sm"></div>
            </div>
          </div>
          <span className="text-white text-xl font-semibold">CRVKN</span>
        </div>

        <div className="max-w-4xl mx-auto">
          {/* User Input */}
          <div className="flex justify-end mb-6">
            <div className="bg-white rounded-full px-6 py-3 max-w-md">
              <span className="text-stone-800">{codeInput.substring(0, 50)}...</span>
            </div>
          </div>

          {/* Review Content */}
          <div className="space-y-6 text-white">
            <h2 className="text-xl font-medium">Your line</h2>
            
            <div className="bg-stone-700/50 rounded-xl p-6">
              <div className="text-sm text-stone-300 mb-2">{detectLanguage(codeInput)}</div>
              <pre className="text-white font-mono">{codeInput}</pre>
            </div>

            <div className="space-y-4">
              <p>{reviewData.review}</p>
              
              {reviewData.optimized_code && (
                <div className="bg-stone-700/50 rounded-xl p-6">
                  <div className="text-sm text-stone-300 mb-2">{detectLanguage(reviewData.optimized_code)}</div>
                  <pre className="text-white font-mono">{reviewData.optimized_code}</pre>
                </div>
              )}

              {reviewData.explanation && (
                <p>{reviewData.explanation}</p>
              )}

              {reviewData.security_issues && (
                <div className="bg-stone-700/50 rounded-xl p-6">
                  <div className="text-sm text-stone-300 mb-2">Security Analysis</div>
                  <p className="text-white">{reviewData.security_issues}</p>
                </div>
              )}
            </div>

            {/* Feedback Section */}
            <div className="flex items-start gap-4 pt-6">
              <button 
                onClick={() => setShowFeedback(!showFeedback)}
                className="flex-shrink-0 p-2 hover:bg-stone-700/50 rounded-lg transition-colors"
              >
                <ThumbsDown className="w-6 h-6 text-stone-400" />
              </button>

              {showFeedback && (
                <div className="bg-stone-700/30 rounded-2xl p-6 flex-1">
                  <div className="grid grid-cols-3 gap-3">
                    {feedbackTags.map((tag, index) => (
                      <button
                        key={index}
                        onClick={() => handleFeedbackSubmit('negative', tag)}
                        className="bg-stone-600/50 hover:bg-stone-600 text-stone-200 px-4 py-2 rounded-full text-sm transition-colors"
                      >
                        {tag}
                      </button>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-stone-800 to-stone-900 p-6">
      {/* Logo */}
      <div className="flex items-center gap-3 mb-20">
        <div className="w-12 h-12 bg-white rounded-full flex items-center justify-center">
          <div className="w-8 h-8 bg-stone-800 rounded flex items-center justify-center">
            <div className="w-4 h-4 border-2 border-white rounded-sm"></div>
          </div>
        </div>
        <span className="text-white text-xl font-semibold">CRVKN</span>
      </div>

      {/* Main Input Area */}
      <div className="flex flex-col items-center justify-center min-h-[60vh]">
        <div className="relative w-full max-w-2xl">
          {/* Main Input */}
          <div className="bg-white rounded-full flex items-center px-6 py-4 shadow-lg">
            <button 
              onClick={() => setShowDropdown(!showDropdown)}
              className="flex-shrink-0 mr-4"
            >
              <Plus className="w-6 h-6 text-stone-800" />
            </button>
            
            <input
              type="text"
              placeholder="Write Anything"
              value={codeInput}
              onChange={(e) => setCodeInput(e.target.value)}
              className="flex-1 bg-transparent text-stone-800 placeholder-stone-400 outline-none text-lg"
              onKeyPress={(e) => e.key === 'Enter' && handleSubmitCode()}
            />
            
            {codeInput && (
              <button
                onClick={handleSubmitCode}
                disabled={isLoading}
                className="ml-4 bg-stone-800 text-white px-6 py-2 rounded-full hover:bg-stone-700 transition-colors disabled:opacity-50"
              >
                {isLoading ? (
                  <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                ) : (
                  <span>send</span>
                )}
              </button>
            )}
          </div>

          {/* Dropdown Menu */}
          {showDropdown && (
            <div className="absolute top-full left-0 mt-2 bg-white rounded-2xl shadow-lg p-4 w-64 z-10">
              <button
                onClick={() => {
                  // Handle URL upload logic here
                  setShowDropdown(false);
                }}
                className="w-full flex items-center gap-3 p-3 text-left hover:bg-gray-100 rounded-lg transition-colors"
              >
                <Link className="w-5 h-5 text-stone-600" />
                <span className="text-stone-800">Upload URL</span>
              </button>
              
              <button
                onClick={() => {
                  fileInputRef.current?.click();
                  setShowDropdown(false);
                }}
                className="w-full flex items-center gap-3 p-3 text-left hover:bg-gray-100 rounded-lg transition-colors"
              >
                <Upload className="w-5 h-5 text-stone-600" />
                <span className="text-stone-800">Upload File</span>
              </button>
            </div>
          )}

          {/* Hidden File Input */}
          <input
            ref={fileInputRef}
            type="file"
            accept=".txt,.py,.js,.java,.cpp,.c,.html,.css,.json"
            onChange={handleFileUpload}
            className="hidden"
          />
        </div>
      </div>
    </div>
  );
};

export default CodeReviewApp;
